<?phpfunction WebuneFullUrl() {	$webune_s = empty($_SERVER["HTTPS"]) ? '' : ($_SERVER["HTTPS"] == "on") ? "s" : "";	$WebuneProtocol = substr(strtolower($_SERVER["SERVER_PROTOCOL"]), 0, strpos(strtolower($_SERVER["SERVER_PROTOCOL"]), "/")) . $webune_s;	$WebunePort = ($_SERVER["SERVER_PORT"] == "80") ? "" : (":".$_SERVER["SERVER_PORT"]);	return $WebuneProtocol . "://" . $_SERVER['SERVER_NAME'] . $WebunePort . $_SERVER['REQUEST_URI'];}function getPage(){	$currentFile = $_SERVER["PHP_SELF"];	$parts = Explode('/', $currentFile);	$page = $parts[count($parts) - 1];	return $page;}function getCurrentDate($format){	date_default_timezone_set("Asia/Colombo");	return  date($format,time());}$url=WebuneFullUrl();//Find weather localhost or other online server$pos=strpos(strval($url),"localhost");$host_name=substr(strval($url),$pos,9);if($host_name!="localhost"){	define('HTTP_SERVER', 'http://ntg.lk');	define('HTTP_SERVER_CSS', HTTP_SERVER.'/css/st_admin_style.css');}else{	define('HTTP_SERVER', 'http://localhost//Trans_Gulf');	define('HTTP_SERVER_CSS', 'css/admin_style.css');}date_default_timezone_set("Asia/Colombo");define('CURRENCY','$');require_once('src/utility/DBConstants.class.php');require_once('src/utility/BuildView.class.php');require_once('src/utility/FormBuilder.class.php');require_once('src/utility/WebConstants.class.php');require_once('src/platform/SessionManager.class.php');require_once('src/platform/DBConnection.class.php');require_once('src/platform/Controller.class.php');require_once('src/platform/ComboBuilder.class.php');require_once('src/platform/Primery_key.class.php');require_once('src/platform/Upload.class.php');require_once('src/platform/Crypt.class.php');require_once('src/dao/Admin.class.php');require_once('src/form/AdminForm.class.php');require_once('src/controller/AdminController.class.php');require_once('src/dao/User.class.php');require_once('s