<?phpclass DBConnection{    private $resultSet = "";    private $conn = "";    function __construct()    {        $hostname_conn = DBConstants::HOST_NAME;        $database_conn = DBConstants::DATABASE_NAME;        $username_conn = DBConstants::USER_NAME;        $password_conn = DBConstants::PASSWORD;        $this->conn = new mysqli() or trigger_error(mysqli_error($this->conn), E_USER_ERROR);        $this->conn->connect($hostname_conn, $username_conn, $password_conn, $database_conn);    }    public function executeQuery($query)    {        $this->resultSet = mysqli_query($this->conn, $query) or die(mysqli_error($this->conn));    }    public function nextRecord()    {        return mysqli_fetch_assoc($this->resultSet);    }    public function countRows()    {        return mysqli_num_rows($this->resultSet);    }    public function beginTransaction()    {        //@mysql_query("BEGIN");        mysqli_query($this->conn, "SET AUTOCOMMIT = 0") or die(mysqli_error($this->conn));    }    public function endTransaction()    {        //@mysql_query("COMMIT");        mysqli_query($this->conn, "SET AUTOCOMMIT = 1", $this->conn) or die(mysqli_error($this->conn));        self::close();    }    public function closeRs()    {        mysqli_free_result($this->resultSet);    }    public function close()    {        mysqli_close($this->conn);    }}?>